import React, { useEffect, useState } from "react";
import { Props, TableProps } from "./types";

import {
  Container,
  Header,
  TableContainer,
  TableHeader,
  TableRow,
  TextOutput,
  Title,
} from "./styles";

const Output: React.FC<Props> = ({ csv, header, body }) => {
  const [headerData, setHeaderData] = useState<string[]>([]);
  const [bodyData, setBodyData] = useState<string[]>([]);

  useEffect(() => {
    const headerTemp = header.split(",");
    setHeaderData(headerTemp);
    console.log("headerTemp", headerTemp);

    const bodyTemp = body.split(",");
    setBodyData(bodyTemp);
    console.log("bodyTemp", bodyTemp);
  }, []);

  return (
    <Container>
      <Header>
        <Title>O CSV Ã©:</Title>
      </Header>

      <TextOutput value={csv} disabled />
      <Table header={headerData} body={bodyData} />
    </Container>
  );
};

const Table: React.FC<TableProps> = ({ header, body }) => {
  return (
    <>
      <TableContainer>
        <tbody>
          {header.map((headerElement) => (
            <>
              <TableHeader>{headerElement}</TableHeader>
              {body.map((bodyElement) => (
                <>
                  <TableRow>{bodyElement}</TableRow>
                </>
              ))}
            </>
          ))}
          {/* 
          {body.map((element) => (
            <TableRow>{element}</TableRow>
          ))} */}
        </tbody>
      </TableContainer>
    </>
  );
};
export default Output;
