import React, { useEffect, useState } from "react";
import { Props, TableProps } from "./types";

import {
  Container,
  Header,
  TableContainer,
  Th,
  Tr,
  TextOutput,
  Title,
  Td,
} from "./styles";

const Output: React.FC<Props> = ({ csv, header, body, obj }) => {
  return (
    <Container>
      <Header>
        <Title>O CSV Ã©:</Title>
      </Header>

      <TextOutput value={csv} disabled />
      <Table obj={obj} />
    </Container>
  );
};

const Table: React.FC<TableProps> = ({ obj }) => {
  const [header, setHeader] = useState<string[]>([]);
  useEffect(() => {
    console.log("OBJ", obj);
    const temp = Object.keys(obj[0]);
    setHeader(temp);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  return (
    <>
      <TableContainer>
        <thead>
          {header.map((element, index) => (
            <Th>{element}</Th>
          ))}
        </thead>
        <tbody>
          {obj.map((element, index) => (
            <Tr>{console.log("ELEMENT", element)}</Tr>
          ))}
        </tbody>
      </TableContainer>
    </>
  );
};
export default Output;
