import React, { useEffect, useState } from "react";
import { Props, TableProps } from "./types";

import {
  Container,
  Header,
  TableContainer,
  Th,
  Tr,
  TextOutput,
  Title,
  Td,
} from "./styles";

const Output: React.FC<Props> = ({ csv, header, body, obj }) => {
  const [headerData, setHeaderData] = useState<string[]>([]);
  const [bodyData, setBodyData] = useState<string[]>([]);

  useEffect(() => {
    const headerTemp = header.split(",");
    setHeaderData(headerTemp);
    console.log("headerTemp", headerTemp);

    const bodyTemp = body.split(",");
    setBodyData(bodyTemp);
    console.log("bodyTemp", bodyTemp);
  }, []);

  return (
    <Container>
      <Header>
        <Title>O CSV Ã©:</Title>
      </Header>

      <TextOutput value={csv} disabled />
      <Table header={headerData} body={bodyData} obj={obj} />
    </Container>
  );
};

const Table: React.FC<TableProps> = ({ header, body, obj }) => {
  return (
    <>
      <TableContainer>
        <thead>
          {header.map((element) => (
            <Th>{element}</Th>
          ))}
        </thead>
        <tbody>
          {obj.map((element) => (
            <Tr>
              <Td>{element}</Td>{" "}
            </Tr>
          ))}
        </tbody>
      </TableContainer>
    </>
  );
};
export default Output;
