import styled from "styled-components";

export const Container = styled.div`
  padding: 2vw;
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
  span-after {
    content: "\00bb";
    position: absolute;
    opacity: 0;
    top: 0;
    right: -20px;
    transition: 5s;
  }
`;

export const TextOutput = styled.textarea`
  background: #f2f2f2;
  width: 80vw;
  min-height: 20vh;
  border-radius: 10px;
  color: #111;
  padding: 10px;
  resize: none;
`;

export const Title = styled.p`
  color: #f2f2f2;
`;
