import styled from "styled-components";

export const Container = styled.div`
  border: 2px solid white;
  padding: 2vw;
  display: flex;
  justify-content: center;
  flex-direction: column;
`;

export const TextInput = styled.textarea`
  background: gray;
  width: 80vw;
  height: 20vh;
`;

export const Title = styled.p`
  color: #f2f2f2;
`;
